import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import SearchBar from './components/SearchBar';
import SearchResults from './components/SearchResults';
import Watchlist from './components/Watchlist';
import { searchTMDb } from './api/tmdbApi';
import './App.css';

const App = () => {
  const [results, setResults] = useState([]);
  const [watchlist, setWatchlist] = useState(() => {
    // Retrieve watchlist from localStorage
    const savedWatchlist = localStorage.getItem('watchlist');
    return savedWatchlist ? JSON.parse(savedWatchlist) : [];
  });

  // Save watchlist to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('watchlist', JSON.stringify(watchlist));
  }, [watchlist]);

  const handleSearch = async (query) => {
    const data = await searchTMDb(query);
    setResults(data);
  };

  const addToWatchlist = (item) => {
    // Avoid duplicates
    if (!watchlist.some((watchItem) => watchItem.id === item.id)) {
      setWatchlist((prevList) => [...prevList, item]);
    }
  };

  const removeFromWatchlist = (id) => {
    setWatchlist((prevList) => prevList.filter((item) => item.id !== id));
  };

  return (
    <Router>
      <div className="app">
        {/* Link to home page */}
        <h1>
          <Link to="/">MyBingeTracker</Link>
        </h1>
        <Routes>
          <Route
            path="/"
            element={
              <>
                <SearchBar onSearch={handleSearch} />
                <SearchResults results={results} onAddToWatchlist={addToWatchlist} />
                <Watchlist watchlist={watchlist} removeFromWatchlist={removeFromWatchlist} />
              </>
            }
          />
          {/* You can add more routes here if necessary */}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
