import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import SearchBar from './components/SearchBar';
import SearchResults from './components/SearchResults';
import WatchlistPage from './components/WatchlistPage'; // Ensure this is correctly imported
import { searchTMDb } from './api/tmdbApi';
import './App.css';

const App = () => {
  const [results, setResults] = useState([]);
  const [watchlist, setWatchlist] = useState(() => {
    const savedWatchlist = localStorage.getItem('watchlist');
    return savedWatchlist ? JSON.parse(savedWatchlist) : [];
  });

  useEffect(() => {
    localStorage.setItem('watchlist', JSON.stringify(watchlist));
  }, [watchlist]);

  const handleSearch = async (query) => {
    const data = await searchTMDb(query);
    setResults(data);
  };

  const addToWatchlist = (item) => {
    if (!watchlist.some((watchItem) => watchItem.id === item.id)) {
      setWatchlist((prevList) => [...prevList, item]);
    }
  };

  const removeFromWatchlist = (id) => {
    setWatchlist((prevList) => prevList.filter((item) => item.id !== id));
  };

  return (
    <Router>
      <div className="app">
        {/* Use a regular anchor tag to refresh the page */}
        <h1>
          <a href="/">MyBingeTracker</a> {/* This will refresh the page */}
        </h1>
        <nav>
          <Link to="/watchlist">Your Watchlist</Link> {/* Link to Watchlist page */}
        </nav>

        <Routes>
          <Route
            path="/"
            element={
              <>
                <SearchBar onSearch={handleSearch} />
                <SearchResults results={results} onAddToWatchlist={addToWatchlist} />
              </>
            }
          />
          <Route
            path="/watchlist"
            element={<WatchlistPage watchlist={watchlist} removeFromWatchlist={removeFromWatchlist} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
